import React, { useState } from 'react';

function UseState1() {
    const flowers=["Rose","Lilly","Jasmine","Lotus","Sun Flower","Marrie Gold","Tulip"]
    const [Index,setIndex] = useState([0]);

    const handleFlowers=()=>{
        const nextIndex= Index.length;
        setIndex([...Index,nextIndex])
    };
    const handleRefresh=()=>{
        setIndex([]);
    };
    const handleDelete=()=>{
        setIndex([Index]);
    }
    return(

        <div>
            <h1>FLOWERS LIST</h1>
           {Index.map((index,Index)=>(
            
            <p key={Index}>{index+1}. {flowers[index]}</p>
           ))}
           <button  onClick={handleFlowers}> Click Here</button>
           <button onClick={handleRefresh}>Refresh</button>
           <button onClick={handleDelete}>Delete</button>
           
          
        </div>
        
    );
  
 
}
export default UseState1
