import React, { useState } from 'react';

function UseState1() {
    const flowers=["Rose","Lilly","Jasmine","Lotus","Sun Flower","Marrie Gold","Tulip"]
    const [Index,setIndex] = useState([0]);

    const handleFlowers=()=>{
        const nextIndex=(Index[Index.length -1] +1) % flowers.length;
        setIndex([...Index,nextIndex])
    };
    return(

        <div>
           {Index.map((index,Index)=>(
            <p key={Index}>{index}.{flowers[index]}</p>
           ))}
           <button  onClick={handleFlowers}> Click Here</button>
           
          
        </div>
        
    );
  
 
}
export default UseState1
