import { Component } from "react";

class Increment extends Component {
  state = {
    Team: "RCB",
    PlayerName:"",
    TotalRuns:500,
    img: "https://www.iplt20.com/teams/royal-challengers-bengaluru/squad-details/164",
    itemCount: 0,
  };

  // state={
  //   products:[{},{}]
  // }
  Inccount = ()=>{
    this.setState({
        itemCount:this.state.itemCount+1
    })
  }
  Reset = ()=>{
    this.setState({
        itemCount:0
    })
  }
  Del = ()=>{
    this.setState({
        itemCount:this.state.itemCount-1
    })
  }
  render() {
    return (
      <div>
        <img src={this.state.img} alt="Virat Kohli" height={400} width={400} />
        <p>{this.state.item}</p>
        {this.state.itemCount > 0 && (
          <>
            <p>No. of items added:<b> {this.state.itemCount}</b></p>
            <button onClick={this.Del}>Delete</button>
          </>
         )} 
        <button onClick={this.Inccount}>Add to Cart</button>
       {this.state.itemCount>0 && (
        <>
        <button onClick={this.Reset}>Reset Cart</button>

        </>
       )
       }
      </div>
    );
  }
}
export default Increment;
