import React, { useState } from 'react';

function UseState1() {
    const flowers=["Rose","Lilly","Jasmine","Lotus","Sun Flower","Marrie Gold","Tulip"]
    const [flower, setFlowers] = useState(["Rose"]);
    const handleFlowers=()=>{
        const nextIndex= flowers.length;
        setFlowers([...flower,flowers[nextIndex]])
    };
    const handleRefresh=()=>{
        setFlowers([]);
    };
    const handleDelete=()=>{
        setFlowers([flowers.slice(0,-1)]);
    }
    return(

        <div>
            <h1>FLOWERS LIST</h1>
           {flowers.map((flower,index)=>(
            
            <p key={flowers}>{index+1}. {flower}</p>
           ))}
           <button  onClick={handleFlowers}> Click Here</button>
           <button onClick={handleRefresh}>Refresh</button>
           <button onClick={handleDelete}>Delete</button>
           
          
        </div>
        
    );
  
 
}
export default UseState1
