import React, { Component } from "react";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";

class ProductCategory extends Component {
  constructor() {
    super();
    this.state = {
      products: [],
      selectedCategory: null,
      dataFetched: false, 
    };
  }

  fetchData = async () => {
    const response = await axios.get("https://fakestoreapi.com/products");
    this.setState({
      products: response.data,
      dataFetched: true, 
    });
  };

  fetchCategory = async (category) => {
    const response = await axios.get(`https://fakestoreapi.com/products/category/${category}`);
    this.setState({
      selectedCategory: response.data,
    });
  };

  componentDidMount() {
    if (!this.state.dataFetched) {
      this.fetchData(); 
    }
  }

  render() {
    const { selectedCategory, products } = this.state;

    return (
      <div className="container">
        <div className="category-buttons">
          {products.map((item) => (
            <Button key={item.id} onClick={() => this.fetchCategory(item.category)} variant="success">
              {item.category}
            </Button>
          ))}
        </div>

        <div className="category-card">
          {selectedCategory ? (
            <Card style={{ width: "18rem" }}>
              <Card.Img variant="top" src={selectedCategory.image} height={250} />
              <Card.Body>
                <Card.Title>{selectedCategory.title}</Card.Title>
                <Card.Text>
                  <b>Id:</b> {selectedCategory.id}
                </Card.Text>
                <Card.Text>
                  <b>Category:</b> {selectedCategory.category}
                </Card.Text>
              </Card.Body>
            </Card>
          ) : (
            <h2>No category selected</h2>
          )}
        </div>
      </div>
    );
  }
}

export default ProductCategory;
