import { useState } from "react";



function Uncontrolled(){
    const[name,setName]=useState("");
    const[password,setPassword]=("");


    function handleSubmit(){
        const result=name.length<10 && password.length<10
        alert("LOGIN SUCCESS");

    }
    handleSubmit(result)
    return(
        <>
        <div>
            <h1>FORMS USING UNCONTROLLED COMPONENTS</h1>
            <form>
                <label>USERNAME:</label>
                <input type="text" name="name" value={name} onChange={(form)=>setName(form.target.value)}></input>
                <label>PASSWORD:</label>
                <input type="text" name="name" value={name} onChange={(form)=>setPassword(form.target.value)}></input>
                <button  type="submit" onClick={handleSubmit}>SUBMIT</button>

            </form>

        </div>
        </>

    );
}
export default Uncontrolled
