import React, { useState } from 'react';

function UseState() {
  
  //const [flower, setFlower] = useState("rose");
  var flowerStateVariable = useState('rose'); 
  var flower = flowerStateVariable[0]; // First item in a pair
  var setFlower = flowerStateVariable[1]; // Second item in a pair

  return (
    <div>
      <p>{flower}</p>
      <button onClick={() => setFlower(flower)}>
        Click me
      </button>
    </div>
  );
}
export default UseState
