import { Component } from "react";
import React from 'react';
import { MDBCarousel, MDBCarouselItem } from 'mdb-react-ui-kit';
//import Carousel from "mdb-react-ui-kit";

class Carousell extends Component {
  state = {
    imgs: [
        'https://mdbootstrap.com/img/new/slides/041.jpg',
      'https://mdbootstrap.com/img/new/slides/042.jpg',
      'https://mdbootstrap.com/img/new/slides/043.jpg',


    ],
  };

  add = () => {
    const newimg = prompt("Enter image address");
    if (newimg) { 
      this.setState(prevState => ({
        imgs: [
          ...prevState.imgs,
          newimg,
        ],
      }));
    }


  };
  render() {
    return (
      <>
        <>
        <MDBCarousel showControls showIndicators touch={false}>
      <MDBCarouselItem itemId={1}>
        <img src='https://mdbootstrap.com/img/new/slides/041.jpg' className='d-block w-100' alt='...' height={500} />
      </MDBCarouselItem>
      <MDBCarouselItem itemId={2}>
        <img src='https://mdbootstrap.com/img/new/slides/042.jpg' className='d-block w-100' alt='...' height={500}/>
      </MDBCarouselItem>
      <MDBCarouselItem itemId={3}>
        <img src='https://mdbootstrap.com/img/new/slides/043.jpg' className='d-block w-100' alt='...' height={500} />
      </MDBCarouselItem>
     
    </MDBCarousel>
      </>
        <div style={{textAlign:"center", marginTop:"50px"}}>
        <button onClick={this.add}>Add image</button>
        </div>
      </>
    );
  }
}

export default Carousell;
